import { addVehicle, getUserList } from "./api.js";
export default {
  name: "add",
  components: {},
  data() {
    return {
      submitLoading: false,
      // 表单提交状态
      form: {
        // 添加或编辑表单对象初始化数据
        carNumber: "",
        drivingLicense: "",
        carType: "",
        ownerId: "",
        ownerName: ""
      },
      // 表单验证规则
      formValidate: {},
      userList: []
    };
  },
  methods: {
    init() {
      this.getUserListFx();
    },
    getUserListFx() {
      var that = this;
      that.userList = [];
      getUserList({
        type: 0
      }).then(res => {
        if (res.success) {
          that.userList = res.result;
        }
      });
    },
    handleReset() {
      this.$refs.form.resetFields();
    },
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          addVehicle(this.form).then(res => {
            this.submitLoading = false;
            if (res.success) {
              this.$Message.success("操作成功");
              this.submited();
            }
          });
        }
      });
    },
    close() {
      this.$emit("close", true);
    },
    submited() {
      this.$emit("submited", true);
    }
  },
  mounted() {
    this.init();
  }
};
